import React from 'react';
import Link from 'next/link';
import ComponentSlider from "@kapost/react-component-slider";

const renderLeftArrow = () => <i className="fa fa-caret-left" />;
const renderRightArrow = () => <i className="fa fa-caret-right" />;

export default function TaskAreaView() {

    return (
        <div className='taskArea'>
            <ComponentSlider
          renderLeftArrow={renderLeftArrow}
          renderRightArrow={renderRightArrow}
          >            <div className='card card--mb20'>
            <div className='grid-inner'>
            <h4 className='heading heading--h4'>Tasks Area</h4>
            </div>
                <div className='card__content'>
                    <ul className='taskArea__listing'>
                        {
                            ['Lobby', 'Reception', 'Washroom','Lobby', 'Reception', 'Washroom', 'Cafe Area'].map((item) => {
                                return (
                                    <Link href='/listing'>
                                        <li className='taskArea__listing__item'>
                                            <img
                                                src={`/images/${item}.jpg`}
                                                 height='70%' width='80%'
                                                alt='img'
                                            />
                                            <div className='taskArea__listing__label'>{item}</div>
                                        </li>
                                    </Link>
                                );
                            })
                        }
                    </ul>
                </div>
            </div>
            </ComponentSlider>

        </div>
    );
}
